mapper.update:
update b_form_manager
set form_type = #{formType,jdbcType=INTEGER},
  approver = #{approver,jdbcType=INTEGER},
  approver_lv = #{approverLv,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, form_type, approver, approver_lv
from b_form_manager
where id = #{id,jdbcType=INTEGER}
