mapper.update:
update b_power_type
set power_id = #{powerId,jdbcType=INTEGER},
  power_name = #{powerName,jdbcType=VARCHAR},
  form_type = #{formType,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, power_id, power_name, form_type
from b_power_type
where id = #{id,jdbcType=INTEGER}
