mapper.update:
update b_user_info


    real_name = #{realName,jdbcType=VARCHAR},


    school_id = #{schoolId,jdbcType=VARCHAR},


    e_mail = #{eMail,jdbcType=VARCHAR},


    tel = #{tel,jdbcType=VARCHAR}


where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, real_name, school_id, e_mail, tel
from b_user_info
where id = #{id,jdbcType=INTEGER}
